import React, { createContext, useContext, useState, useEffect } from 'react';
import { authService } from '../services/supabaseService';
import toast from 'react-hot-toast';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [currentUser, setCurrentUser] = useState(null);
  const [userRole, setUserRole] = useState('usuario');
  const [loading, setLoading] = useState(true);

  // Registro de usuario
  async function signup(email, password, displayName) {
    try {
      const result = await authService.signUp(email, password, displayName);
      
      toast.success('Cuenta creada exitosamente. Revisa tu email para confirmar.');
      return result;
    } catch (error) {
      console.error('Error en registro:', error);
      toast.error(getErrorMessage(error.message));
      throw error;
    }
  }

  // Login
  async function login(email, password) {
    try {
      const result = await authService.signIn(email, password);
      
      toast.success('Inicio de sesi√≥n exitoso');
      return result;
    } catch (error) {
      console.error('Error en login:', error);
      toast.error(getErrorMessage(error.message));
      throw error;
    }
  }

  // Logout
  async function logout() {
    try {
      console.log('üö™ AuthContext: Iniciando logout...');
      await authService.signOut();
      
      // Forzar limpieza del estado
      setCurrentUser(null);
      setUserRole('usuario');
      
      console.log('‚úÖ AuthContext: Logout exitoso');
      toast.success('Sesi√≥n cerrada');
      
    } catch (error) {
      console.error('‚ùå Error en logout:', error);
      
      // Forzar limpieza incluso si hay error
      setCurrentUser(null);
      setUserRole('usuario');
      
      toast.error('Error al cerrar sesi√≥n');
      throw error;
    }
  }

  // Recuperar contrase√±a
  async function resetPassword(email) {
    try {
      await authService.resetPassword(email);
      toast.success('Email de recuperaci√≥n enviado');
    } catch (error) {
      console.error('Error en reset password:', error);
      toast.error(getErrorMessage(error.message));
      throw error;
    }
  }

  // Verificar si es admin
  function isAdmin() {
    console.log('üîç Verificando rol admin:', { 
      userRole, 
      currentUser: currentUser?.email,
      result: userRole === 'admin' 
    });
    return userRole === 'admin';
  }

  // Verificar si es cliente
  function isClient() {
    return userRole === 'cliente' || userRole === 'admin';
  }

  // Obtener mensaje de error en espa√±ol
  function getErrorMessage(errorMessage) {
    const errorMessages = {
      'Usuario no encontrado': 'No existe una cuenta con este email',
      'Contrase√±a incorrecta': 'Contrase√±a incorrecta',
      'El usuario ya existe': 'Este email ya est√° registrado',
      'Email inv√°lido': 'Email inv√°lido',
      'Demasiados intentos': 'Demasiados intentos. Intenta m√°s tarde',
      'Cuenta deshabilitada': 'Esta cuenta ha sido deshabilitada',
      'Operaci√≥n no permitida': 'Operaci√≥n no permitida',
      'Credenciales inv√°lidas': 'Credenciales inv√°lidas'
    };
    return errorMessages[errorMessage] || errorMessage || 'Error desconocido';
  }

  // Verificar sesi√≥n y escuchar cambios de autenticaci√≥n
  useEffect(() => {
    // Obtener sesi√≥n inicial
    const getInitialSession = async () => {
      try {
        const session = await authService.getSession();
        
        if (session?.user) {
          setCurrentUser(session.user);
          console.log('üë§ Usuario autenticado:', session.user.email);
          
          // Forzar admin directamente para tu email
          if (session.user.email === 'oterov101@gmail.com') {
            setUserRole('admin');
            console.log('üîë ADMIN FORZADO para oterov101@gmail.com');
            return;
          }
          
          // Para otros usuarios, obtener perfil normalmente
          try {
            const profile = await authService.getUserProfile(session.user.id);
            console.log('üìã Perfil obtenido:', profile);
            setUserRole(profile?.role || 'cliente');
          } catch (error) {
            console.error('Error obteniendo perfil:', error);
            setUserRole('cliente');
          }
        }
      } catch (error) {
        console.error('‚ùå Error obteniendo sesi√≥n:', error);
      } finally {
        console.log('‚úÖ Carga de autenticaci√≥n completada');
        setLoading(false);
      }
    };

    getInitialSession();

    // Timeout de seguridad
    const timeoutId = setTimeout(() => {
      console.log('‚è∞ Timeout de autenticaci√≥n - forzando carga');
      setLoading(false);
    }, 1500);

    // Escuchar cambios de autenticaci√≥n
    const { data: { subscription } } = authService.onAuthStateChange(async (event, session) => {
      if (event === 'SIGNED_IN' && session?.user) {
        setCurrentUser(session.user);
        
        // Forzar admin directamente para tu email
        if (session.user.email === 'oterov101@gmail.com') {
          setUserRole('admin');
          console.log('üîë ADMIN FORZADO en onChange para oterov101@gmail.com');
          return;
        }
        
        // Para otros usuarios, obtener perfil normalmente
        try {
          const profile = await authService.getUserProfile(session.user.id);
          setUserRole(profile?.role || 'cliente');
        } catch (error) {
          console.error('Error obteniendo perfil:', error);
          setUserRole('cliente');
        }
      } else if (event === 'SIGNED_OUT') {
        setCurrentUser(null);
        setUserRole('usuario');
      }
    });

    return () => {
      subscription.unsubscribe();
      clearTimeout(timeoutId);
    };
  }, []);

  const value = {
    currentUser,
    userRole,
    signup,
    login,
    logout,
    resetPassword,
    isAdmin,
    isClient,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
}
