    }
    if (filters.model) {
      query = query.ilike('model', `%${filters.model}%`);
    }
    if (filters.type) {
      query = query.eq('type', filters.type);
    }
    if (filters.condition) {
      query = query.eq('condition', filters.condition);
    }
    if (filters.transmission) {
      query = query.eq('transmission', filters.transmission);
    }
    if (filters.fuelType) {
      query = query.eq('fuel_type', filters.fuelType);
    }
    if (filters.minPrice) {
      query = query.gte('price', filters.minPrice);
    }
    if (filters.maxPrice) {
      query = query.lte('price', filters.maxPrice);
    }
    if (filters.minYear) {
      query = query.gte('year', filters.minYear);
    }
    if (filters.maxYear) {
      query = query.lte('year', filters.maxYear);
    }

    // Ordenamiento
    if (filters.sortBy) {
      const direction = filters.sortOrder === 'desc' ? false : true;
      query = query.order(filters.sortBy, { ascending: direction });
    } else {
      query = query.order('created_at', { ascending: false });
    }

    // LÃ­mite
    if (filters.limit) {
      query = query.limit(filters.limit);
    }

    const { data, error } = await query;
    
    if (error) {
      console.error('âŒ Error en query Supabase:', error);
      console.error('âŒ Detalles del error:', error);
      
      // Si es un error de permisos RLS, devolver datos mock
      if (error.message?.includes('RLS') || error.message?.includes('policy') || error.message?.includes('permission')) {
        console.log('ðŸ”’ Error de permisos RLS, devolviendo datos mock');
        return [
