import React, { useState, useEffect } from 'react';
import { X, Upload, Plus, Trash2, ChevronUp, ChevronDown, Check } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { createVehicle, updateVehicle } from '../../services/vehicleService';
import toast from 'react-hot-toast';

const VehicleForm = ({ vehicle, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    brand: '',
    model: '',
    version: '',
    year: new Date().getFullYear(),
    kilometers: 0,
    price: 0,
    condition: 'used',
    transmission: 'manual',
    fuelType: 'gasoline',
    color: '',
    type: 'sedan',
    location: '',
    engine: '',
    power: '',
    features: [],
    featured: false,
    promotion: false
  });
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [newFeature, setNewFeature] = useState('');
  const [showFeatureDropdown, setShowFeatureDropdown] = useState(false);

  useEffect(() => {
    if (vehicle) {
      setFormData({
        brand: vehicle.brand || '',
        model: vehicle.model || '',
        version: vehicle.version || '',
        year: vehicle.year || new Date().getFullYear(),
        kilometers: vehicle.kilometers || 0,
        price: vehicle.price || 0,
        condition: vehicle.condition || 'used',
        transmission: vehicle.transmission || 'manual',
        fuelType: vehicle.fuelType || 'gasoline',
        color: vehicle.color || '',
        type: vehicle.type || 'sedan',
        location: vehicle.location || '',
        engine: vehicle.engine || '',
        power: vehicle.power || '',
        features: vehicle.features || [],
        featured: vehicle.featured || false,
        promotion: vehicle.promotion || false
      });
    }
  }, [vehicle]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setImages(prev => [...prev, ...files]);
  };

  const removeImage = (index) => {
    setImages(prev => prev.filter((_, i) => i !== index));
  };

  const addFeature = () => {
    if (newFeature.trim()) {
      setFormData(prev => ({
        ...prev,
        features: [...prev.features, newFeature.trim()]
      }));
      setNewFeature('');
    }
  };

  const removeFeature = (index) => {
    setFormData(prev => ({
      ...prev,
      features: prev.features.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      // Transformar datos para que coincidan con la estructura de Supabase
      const vehicleData = {
        brand: formData.brand,
        model: formData.model,
        year: parseInt(formData.year),
        kilometers: parseInt(formData.kilometers),
        price: parseFloat(formData.price),
        price_ars: parseFloat(formData.price) * 1000, // Conversión aproximada USD a ARS
        condition: formData.condition,
        transmission: formData.transmission,
        fuel_type: formData.fuelType, // Corregir nombre del campo
        color: formData.color,
        type: formData.type,
        location: formData.location,
        engine: formData.engine,
        power: formData.power,
        features: formData.features,
        is_featured: formData.featured, // Corregir nombre del campo
        is_promotion: formData.promotion, // Corregir nombre del campo
        images: []
      };

      console.log('📝 Datos del vehículo a guardar:', vehicleData);

      if (vehicle) {
        // Actualizar vehículo existente
        await updateVehicle(vehicle.id, vehicleData);
        toast.success('Vehículo actualizado exitosamente');
      } else {
        // Crear nuevo vehículo
        if (images.length === 0) {
          toast.error('Debes subir al menos una imagen');
          setLoading(false);
          return;
        }
        await createVehicle(vehicleData);
        toast.success('Vehículo creado exitosamente');
      }
      onSuccess();
    } catch (error) {
      console.error('❌ Error guardando vehículo:', error);
      toast.error(`Error al guardar el vehículo: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const brands = [
    'Toyota', 'Honda', 'Ford', 'Chevrolet', 'Volkswagen',
    'BMW', 'Mercedes-Benz', 'Audi', 'Nissan', 'Hyundai',
    'Kia', 'Mazda', 'Subaru', 'Mitsubishi', 'Peugeot',
    'Renault', 'Fiat', 'Volvo', 'Jaguar', 'Land Rover'
  ];

  const vehicleTypes = [
    { value: 'sedan', label: 'Sedán' },
    { value: 'suv', label: 'SUV' },
    { value: 'pickup', label: 'Pick-Up' },
    { value: 'hatchback', label: 'Hatchback' },
    { value: 'coupe', label: 'Coupé' },
    { value: 'cabriolet', label: 'Cabriolet' },
    { value: 'premium', label: 'Premium' }
  ];

  const conditions = [
    { value: 'new', label: 'Nuevo' },
    { value: 'used', label: 'Usado' }
  ];

  const transmissions = [
    { value: 'manual', label: 'Manual' },
    { value: 'automatic', label: 'Automático' },
    { value: 'cvt', label: 'CVT' }
  ];

  const fuelTypes = [
    { value: 'gasoline', label: 'Gasolina' },
    { value: 'diesel', label: 'Diésel' },
    { value: 'hybrid', label: 'Híbrido' },
    { value: 'electric', label: 'Eléctrico' },
    { value: 'lpg', label: 'GLP' }
  ];

  const years = Array.from({ length: 25 }, (_, i) => new Date().getFullYear() - i);

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
      >
        <motion.div
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ scale: 0.9, opacity: 0 }}
          className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto"
        >
          <div className="p-6 border-b border-gray-200">
            <div className="flex justify-between items-center">
              <h2 className="text-2xl font-bold text-gray-900">
                {vehicle ? 'Editar Vehículo' : 'Agregar Vehículo'}
              </h2>
              <button
                onClick={onClose}
                className="text-gray-400 hover:text-gray-600"
              >
                <X className="w-6 h-6" />
              </button>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="p-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Información básica */}
              <div className="space-y-4">
                <h3 className="text-lg font-medium text-gray-900">Información Básica</h3>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Marca *
                  </label>
                  <select
                    name="brand"
                    value={formData.brand}
                    onChange={handleChange}
                    required
                    className="select-field"
                  >
                    <option value="">Seleccionar marca</option>
                    {brands.map(brand => (
                      <option key={brand} value={brand}>{brand}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Modelo *
                  </label>
                  <input
                    type="text"
                    name="model"
                    value={formData.model}
                    onChange={handleChange}
                    required
                    className="input-field"
                    placeholder="Ej: Corolla"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Versión
                  </label>
                  <input
                    type="text"
                    name="version"
                    value={formData.version}
                    onChange={handleChange}
                    className="input-field"
                    placeholder="Ej: XEI 2.0"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Año *
                    </label>
                    <select
                      name="year"
                      value={formData.year}
                      onChange={handleChange}
                      required
                      className="select-field"
                    >
                      {years.map(year => (
                        <option key={year} value={year}>{year}</option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Kilometraje *
                    </label>
                    <input
                      type="number"
                      name="kilometers"
                      value={formData.kilometers}
                      onChange={handleChange}
                      required
                      className="input-field"
                      placeholder="0"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Precio (USD) *
                  </label>
                  <input
                    type="number"
                    name="price"
                    value={formData.price}
                    onChange={handleChange}
                    required
                    className="input-field"
                    placeholder="0"
                  />
                </div>
              </div>

              {/* Especificaciones */}
              <div className="space-y-4">
                <h3 className="text-lg font-medium text-gray-900">Especificaciones</h3>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Condición *
                    </label>
                    <select
                      name="condition"
                      value={formData.condition}
                      onChange={handleChange}
                      required
                      className="select-field"
                    >
                      {conditions.map(condition => (
                        <option key={condition.value} value={condition.value}>
                          {condition.label}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Tipo *
                    </label>
                    <select
                      name="type"
                      value={formData.type}
                      onChange={handleChange}
                      required
                      className="select-field"
                    >
                      {vehicleTypes.map(type => (
                        <option key={type.value} value={type.value}>
                          {type.label}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Transmisión *
                    </label>
                    <select
                      name="transmission"
                      value={formData.transmission}
                      onChange={handleChange}
                      required
                      className="select-field"
                    >
                      {transmissions.map(transmission => (
                        <option key={transmission.value} value={transmission.value}>
                          {transmission.label}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Combustible *
                    </label>
                    <select
                      name="fuelType"
                      value={formData.fuelType}
                      onChange={handleChange}
                      required
                      className="select-field"
                    >
                      {fuelTypes.map(fuelType => (
                        <option key={fuelType.value} value={fuelType.value}>
                          {fuelType.label}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Color
                  </label>
                  <input
                    type="text"
                    name="color"
                    value={formData.color}
                    onChange={handleChange}
                    className="input-field"
                    placeholder="Ej: Blanco"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Ubicación
                  </label>
                  <input
                    type="text"
                    name="location"
                    value={formData.location}
                    onChange={handleChange}
                    className="input-field"
                    placeholder="Ej: CABA"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Motor
                    </label>
                    <input
                      type="text"
                      name="engine"
                      value={formData.engine}
                      onChange={handleChange}
                      className="input-field"
                      placeholder="Ej: 2.0L"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Potencia
                    </label>
                    <input
                      type="text"
                      name="power"
                      value={formData.power}
                      onChange={handleChange}
                      className="input-field"
                      placeholder="Ej: 150 HP"
                    />
                  </div>
                </div>
              </div>
            </div>

            {/* Características */}
            <div className="mt-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Características</h3>
              <div className="space-y-4">
                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={newFeature}
                    onChange={(e) => setNewFeature(e.target.value)}
                    className="input-field flex-1"
                    placeholder="Agregar característica"
                    onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addFeature())}
                  />
                  <button
                    type="button"
                    onClick={addFeature}
                    className="btn-secondary flex items-center"
                  >
                    <Plus className="w-4 h-4 mr-1" />
                    Agregar
                  </button>
                </div>

                <div className="flex flex-wrap gap-2">
                  {formData.features.map((feature, index) => (
                    <span
                      key={index}
                      className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-primary-100 text-primary-800"
                    >
                      {feature}
                      <button
                        type="button"
                        onClick={() => removeFeature(index)}
                        className="ml-2 hover:text-primary-600"
                      >
                        <X className="w-3 h-3" />
                      </button>
                    </span>
                  ))}
                </div>
              </div>
            </div>

            {/* Opciones */}
            <div className="mt-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Opciones</h3>
              <div className="space-y-4">
                <div className="flex items-center">
                  <input
                    type="checkbox"
                    name="featured"
                    checked={formData.featured}
                    onChange={handleChange}
                    className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                  />
                  <label className="ml-2 block text-sm text-gray-900">
                    Marcar como destacado
                  </label>
                </div>

                <div className="flex items-center">
                  <input
                    type="checkbox"
                    name="promotion"
                    checked={formData.promotion}
                    onChange={handleChange}
                    className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                  />
                  <label className="ml-2 block text-sm text-gray-900">
                    Marcar como promoción
                  </label>
                </div>
              </div>
            </div>

            {/* Imágenes */}
            <div className="mt-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Imágenes</h3>
              <div className="space-y-4">
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
                  <div className="text-center">
                    <Upload className="mx-auto h-12 w-12 text-gray-400" />
                    <div className="mt-2">
                      <label htmlFor="images" className="cursor-pointer">
                        <span className="text-primary-600 hover:text-primary-500 font-medium">
                          Subir imágenes
                        </span>
                        <input
                          id="images"
                          type="file"
                          multiple
                          accept="image/*"
                          onChange={handleImageChange}
                          className="hidden"
                        />
                      </label>
                      <p className="text-xs text-gray-500 mt-1">
                        PNG, JPG, GIF hasta 10MB cada una
                      </p>
                    </div>
                  </div>
                </div>

                {images.length > 0 && (
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    {images.map((image, index) => (
                      <div key={index} className="relative">
                        <img
                          src={URL.createObjectURL(image)}
                          alt={`Imagen ${index + 1}`}
                          className="w-full h-24 object-cover rounded-lg"
                        />
                        <button
                          type="button"
                          onClick={() => removeImage(index)}
                          className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                        >
                          <Trash2 className="w-3 h-3" />
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Botones */}
            <div className="mt-8 flex justify-end space-x-4">
              <button
                type="button"
                onClick={onClose}
                className="btn-secondary"
              >
                Cancelar
              </button>
              <button
                type="submit"
                disabled={loading}
                className="btn-primary"
              >
                {loading ? (
                  <div className="flex items-center">
                    <div className="loading-spinner w-4 h-4 mr-2"></div>
                    Guardando...
                  </div>
                ) : (
                  vehicle ? 'Actualizar Vehículo' : 'Crear Vehículo'
                )}
              </button>
            </div>
          </form>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

export default VehicleForm;
