// Servicio para interactuar con Supabase
import { supabase } from '../supabase/config';

// ==================== SERVICIO DE AUTENTICACIÃ“N ====================
export const authService = {
  // Registrar usuario
  async signUp(email, password, displayName) {
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: {
          display_name: displayName
        }
      }
    });

    if (error) throw error;
    return data;
  },

  // Iniciar sesiÃ³n
  async signIn(email, password) {
    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password
    });

    if (error) throw error;
    return data;
  },

  // Cerrar sesiÃ³n
  async signOut() {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
  },

  // Obtener sesiÃ³n actual
  async getSession() {
    const { data: { session } } = await supabase.auth.getSession();
    return session;
  },

  // Obtener perfil de usuario
  async getUserProfile(userId) {
    const { data, error } = await supabase
      .from('profiles')
      .select('*')
      .eq('id', userId)
      .single();

    if (error) throw error;
    return data;
  },

  // Escuchar cambios de autenticaciÃ³n
  onAuthStateChange(callback) {
    return supabase.auth.onAuthStateChange(callback);
  },

  // Restablecer contraseÃ±a
  async resetPassword(email) {
    const { error } = await supabase.auth.resetPasswordForEmail(email);
    if (error) throw error;
  }
};

// ==================== SERVICIO DE VEHÃCULOS ====================
export const vehicleService = {
  // Obtener todos los vehÃ­culos con filtros
  async getVehicles(filters = {}) {
    console.log('ðŸ” Supabase: Obteniendo vehÃ­culos con filtros:', filters);
    let query = supabase.from('vehicles').select('*');

    // Aplicar filtros
    if (filters.brand) {
      query = query.ilike('brand', `%${filters.brand}%`);
    }
    if (filters.model) {
      query = query.ilike('model', `%${filters.model}%`);
    }
    if (filters.type) {
      query = query.eq('type', filters.type);
    }
    if (filters.condition) {
      query = query.eq('condition', filters.condition);
    }
    if (filters.transmission) {
      query = query.eq('transmission', filters.transmission);
    }
    if (filters.fuelType) {
      query = query.eq('fuel_type', filters.fuelType);
    }
    if (filters.minPrice) {
      query = query.gte('price', filters.minPrice);
    }
    if (filters.maxPrice) {
      query = query.lte('price', filters.maxPrice);
    }
    if (filters.minYear) {
      query = query.gte('year', filters.minYear);
    }
    if (filters.maxYear) {
      query = query.lte('year', filters.maxYear);
    }

    // Ordenamiento
    if (filters.sortBy) {
      const direction = filters.sortOrder === 'desc' ? false : true;
      query = query.order(filters.sortBy, { ascending: direction });
    } else {
      query = query.order('created_at', { ascending: false });
    }

    // LÃ­mite
    if (filters.limit) {
      query = query.limit(filters.limit);
    }

    console.log('ðŸ“¡ Ejecutando query a Supabase...');
    const { data, error } = await query;
    
    console.log('ðŸ” Respuesta completa de Supabase:');
    console.log('- Data:', data);
    console.log('- Error:', error);
    console.log('- Data type:', typeof data);
    console.log('- Data is null:', data === null);
    console.log('- Data is array:', Array.isArray(data));
    
    if (error) {
      console.error('âŒ Error en query Supabase:', error);
      throw error;
    }
    
    console.log('âœ… Datos recibidos de Supabase:', data);
    console.log('ðŸ“Š Cantidad de vehÃ­culos recibidos:', data?.length || 0);
    return data;
  },

  // Obtener vehÃ­culo por ID
  async getVehicleById(id) {
    // Incrementar contador de vistas
    await supabase.rpc('increment_vehicle_views', { vehicle_id: id });

    const { data, error } = await supabase
      .from('vehicles')
      .select('*')
      .eq('id', id)
      .single();

    if (error) throw error;
    return data;
  },

  // Crear nuevo vehÃ­culo
  async createVehicle(vehicleData) {
    const { data, error } = await supabase
      .from('vehicles')
      .insert([{
        brand: vehicleData.brand,
        model: vehicleData.model,
        year: vehicleData.year,
        kilometers: vehicleData.kilometers,
        price: vehicleData.price,
        price_ars: vehicleData.priceARS,
        condition: vehicleData.condition,
        transmission: vehicleData.transmission,
        fuel_type: vehicleData.fuelType,
        color: vehicleData.color,
        type: vehicleData.type,
        location: vehicleData.location,
        engine: vehicleData.engine,
        power: vehicleData.power,
        features: vehicleData.features || [],
        images: vehicleData.images || [],
        is_featured: vehicleData.isFeatured || false,
        is_promotion: vehicleData.isPromotion || false
      }])
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  // Actualizar vehÃ­culo
  async updateVehicle(id, vehicleData) {
    const { data, error } = await supabase
      .from('vehicles')
      .update({
        brand: vehicleData.brand,
        model: vehicleData.model,
        year: vehicleData.year,
        kilometers: vehicleData.kilometers,
        price: vehicleData.price,
        price_ars: vehicleData.priceARS,
        condition: vehicleData.condition,
        transmission: vehicleData.transmission,
        fuel_type: vehicleData.fuelType,
        color: vehicleData.color,
        type: vehicleData.type,
        location: vehicleData.location,
        engine: vehicleData.engine,
        power: vehicleData.power,
        features: vehicleData.features,
        images: vehicleData.images,
        is_featured: vehicleData.isFeatured,
        is_promotion: vehicleData.isPromotion
      })
      .eq('id', id)
      .select()
      .single();

    if (error) throw error;
    return data;
  },

  // Eliminar vehÃ­culo
  async deleteVehicle(id) {
    const { error } = await supabase
      .from('vehicles')
      .delete()
      .eq('id', id);

    if (error) throw error;
  },

  // Obtener vehÃ­culos destacados
  async getFeaturedVehicles(limit = 6) {
    const { data, error } = await supabase
      .from('vehicles')
      .select('*')
      .eq('is_featured', true)
      .order('created_at', { ascending: false })
      .limit(limit);

    if (error) throw error;
    return data;
  },

  // Obtener vehÃ­culos en promociÃ³n
  async getPromotionVehicles(limit = 6) {
    const { data, error } = await supabase
      .from('vehicles')
      .select('*')
      .eq('is_promotion', true)
      .order('created_at', { ascending: false })
      .limit(limit);

    if (error) throw error;
    return data;
  },

  // Obtener vehÃ­culos similares
  async getSimilarVehicles(currentVehicle, limit = 4) {
    const { data, error } = await supabase
      .from('vehicles')
      .select('*')
      .neq('id', currentVehicle.id)
      .or(`brand.eq.${currentVehicle.brand},type.eq.${currentVehicle.type}`)
      .order('created_at', { ascending: false })
      .limit(limit);

    if (error) throw error;
    return data;
  },

  // Subir imagen de vehÃ­culo
  async uploadVehicleImage(file, vehicleId, imageIndex) {
    const fileExt = file.name.split('.').pop();
    const fileName = `${vehicleId}-${imageIndex}.${fileExt}`;
    const filePath = `vehicles/${fileName}`;

    const { data, error } = await supabase.storage
      .from('vehicle-images')
      .upload(filePath, file);

    if (error) throw error;

    // Obtener URL pÃºblica
    const { data: { publicUrl } } = supabase.storage
      .from('vehicle-images')
      .getPublicUrl(filePath);

    return publicUrl;
  }
};

// ==================== SERVICIO DE ESTADÃSTICAS ====================
export const statisticsService = {
  async getStatistics() {
    // Obtener total de vehÃ­culos
    const { count: totalVehicles } = await supabase
      .from('vehicles')
      .select('*', { count: 'exact', head: true });

    // Obtener vehÃ­culos destacados
    const { count: featuredVehicles } = await supabase
      .from('vehicles')
      .select('*', { count: 'exact', head: true })
      .eq('is_featured', true);

    // Obtener vehÃ­culos en promociÃ³n
    const { count: promotionVehicles } = await supabase
      .from('vehicles')
      .select('*', { count: 'exact', head: true })
      .eq('is_promotion', true);

    // Obtener total de vistas
    const { data: viewsData } = await supabase
      .from('vehicles')
      .select('views');

    const totalViews = viewsData?.reduce((sum, vehicle) => sum + (vehicle.views || 0), 0) || 0;

    return {
      totalVehicles: totalVehicles || 0,
      totalViews,
      featuredVehicles: featuredVehicles || 0,
      promotionVehicles: promotionVehicles || 0
    };
  }
};
